import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import PatientHeader from "./PatientHeader";
import PatientStats from "./PatientStats";
import ProgressChart from "./ProgressChart";
import AppointmentHistory from "./AppointmentHistory";
import SessionScheduler from "./SessionScheduler";
import ExerciseAssignment from "./ExerciseAssignment";
import CompletedSessions from "./CompletedSessions";
import { API_BASE_URL } from "../../../config";

interface Patient {
  id: string;
  personalInfo: {
    firstName: string;
    lastName: string;
    age: number;
  };
  recoveryStatus: {
    overallProgress: string;
  };
  medicalInfo: {
    diagnosis: string;
  };
  // Add other patient fields as needed
}

interface Session {
  id: string;
  date: string;
  time: string;
  description: string;
}

const PatientDashboard: React.FC = () => {
  const { patientId } = useParams<{ patientId: string }>();
  const navigate = useNavigate();
  const [patient, setPatient] = useState<Patient | null>(null);
  const [sessions, setSessions] = useState<Session[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchPatientData = async () => {
      try {
        const [patientResponse, sessionsResponse] = await Promise.all([
          fetch(`${API_BASE_URL}/api/patients/${patientId}`),
          fetch(`${API_BASE_URL}/api/sessions/patient/${patientId}`)
        ]);

        if (!patientResponse.ok || !sessionsResponse.ok) {
          throw new Error('Failed to fetch data');
        }

        const patientData = await patientResponse.json();
        const sessionsData = await sessionsResponse.json();

        setPatient(patientData);
        setSessions(sessionsData);
      } catch (err) {
        setError('Error fetching patient data');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    if (patientId) {
      fetchPatientData();
    }
  }, [patientId]);

  const handleReviewSession = (sessionId: number): void => {
    if (patientId) {
      navigate(`/session-review/${sessionId.toString()}`);
    }
  };

  if (loading) {
    return <div className="p-6 text-center">Loading...</div>;
  }

  if (error || !patient) {
    return (
      <div className="p-6 text-center text-red-600">
        {error || 'Patient not found'}
      </div>
    );
  }

  return (
    <div className="bg-white rounded-lg border border-gray-200 shadow-lg p-6 md:p-8 space-y-8">
      <PatientHeader
        name={`${patient.personalInfo.firstName} ${patient.personalInfo.lastName}`}
        patientId={patient.id}
        photoUrl="https://placehold.co/48x48"
      />

      <PatientStats
        age={`${patient.personalInfo.age} years`}
        treatmentStart="Mar 15, 2024"
        progressLevel={String(patient.recoveryStatus.overallProgress)}
      />

      <ProgressChart />

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <AppointmentHistory appointments={sessions} />
        <SessionScheduler />
      </div>

      <ExerciseAssignment />
      <CompletedSessions onReviewSession={handleReviewSession} />

      <div className="flex pt-6 border-t border-gray-200">
        <button
          onClick={() => navigate('/')}
          className="inline-flex items-center px-4 py-2 border border-gray-300 shadow-sm text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
        >
          <svg className="-ml-1 mr-2 h-5 w-5 text-gray-400" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor" aria-hidden="true">
            <path fillRule="evenodd" d="M12.707 5.293a1 1 0 010 1.414L9.414 10l3.293 3.293a1 1 0 01-1.414 1.414l-4-4a1 1 0 010-1.414l4-4a1 1 0 011.414 0z" clipRule="evenodd" />
          </svg>
          Back to Patient List
        </button>
      </div>
    </div>
  );
};

export default PatientDashboard;
